  color: #626262;
}

.cover-img {
  width: 100%;
  border-radius: 6px;
  margin-bottom: 14px;
}

.profile-details {
  background: var(--bg-color);
  padding: 20px;
  border-radius: 4px;
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
}

.profile-details-row {
  display: flex;
  align-items: flex-start;
}

.profile-details-images {
  width: 100px;
  margin-right: 20px;
  border-radius: 6px;
}

.profile-details-row div h3 {
