  font-size: 16px;
  color: var(--text-color);
}

/* Adjust font size of the paragraph */
.subcription p {
  font-size: 12px;
}

/* Adjust size of the advertisment sidebar */
.sidebar-ads {
  width: 100%;
  margin-bottom: 20px;
  border-radius: 4px;
}

/* Align the items in the conversation section of the right sidebar */
.online-list {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
}

/* Adjust the size of the profile pics */
.online-list .online img {
  width: 40px;
  border-radius: 50%;
}

/* Create space between the profile pics and the names  */
.online-list .online {
  width: 40px;
  border-radius: 50%;
  margin-right: 15px;
}

/* Adjust the location of the online symbol to the bottom right of the profile pic */
.online-list .online::after {
  top: unset;
  bottom: 5px;
}

/* Container of the stories */
.story-gallery {
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
}

/* Create the style of the add story */
.story {
  flex-basis: 18%;
  padding-top: 32%;
  position: relative;
  background-position: center;
  background-size: cover;
  border-radius: 10px;
}

/* Adjust the image position and size */
.story img,
.story0 i {
  position: absolute;
  width: 45px;
  border-radius: 50%;
  top: 10px;
  left: 10px;
  border: 3px solid #f96167;
  box-shadow: 0 0 10px rgba(0, 0, 0, 1);
}

/* Adjust the font and size of the words in the story */
.story p {
  position: absolute;
  bottom: 10px;
  width: 100%;
  text-align: center;
  color: #fff;
  font-size: 14px;
}

/* Create the images for every story */
.story0 {
  background-image: linear-gradient(transparent, rgba(0, 0, 0, 0.5)),
    url(https://wallpapercg.com/media/ts_2x/4584.webp);
}

/* Change the location of the add story button */
.story.story0 i {
  font-size: 45px;
  color: #f96167;
  top: unset;
  left: 50%;
  bottom: 40px;
  transform: translateX(-50%);
  border: 0;
  box-shadow: 0 0 10px rgba(0, 0, 0, 1);
}

/* Adjust the size of the write post container */
.write-post-container {
  width: 100%;
  background: var(--bg-color);
  border-radius: 6px;
  padding: 20px;
  color: var(--text-color);
}

.user-profile {
  display: flex;
  align-items: center;
}

/* Adjust size and shape of the profile pic */
.user-profile img {
  width: 45px;
  border-radius: 50%;
  margin-right: 10px;
}

/* Adjust profile name font */
.user-profile p {
  margin-bottom: -5px;
  font-weight: 500;
  color: var(--text-color);
}

/* Adjust the status font */
.user-profile small {
  font-size: 12px;
}

.post-input-container {
  padding-left: 55px;
  padding-top: 20px;
}

/* Adjust the input box size and color */
.post-input-container textarea {
  width: 100%;
  border: 0;
  outline: 0;
  color: var(--text-color);
  border-bottom: 1px solid #ccc;
  background: transparent;
  resize: none;
}

.add-post-links {
  display: flex;
  margin-top: 10px;
}

/* Adjust the write post container links font and color */
.add-post-links a {
  text-decoration: none;
  display: flex;
  align-items: center;
  color: var(--text-color);
  margin-right: 20%;
  font-size: 13px;
}

/* Adjust the size of the write post container links images */
.add-post-links i {
  color: #f96167;
  font-size: 20px;
  margin-right: 10px;
}

/* Adjust previous post size and color */
.post-container {
  width: 100%;
  background: var(--bg-color);
  border-radius: 6px;
  padding: 20px;
  color: var(--text-color);
  margin: 20px 0;
}

/* Adjust data font and color */
.user-profile span {
  font-size: 13px;
  color: #9a9a9a;
}

/* Adjust font size of text in written post */
.post-text {
  color: var(--video-title-color);
  margin: 15px 0;
  font-size: 15px;
}

/* Bold the @s in a written post */
.post-text span {
  color: var(--text-color);
  font-weight: 500;
}

/* Adjust font of hashtags */
.post-text a {
  color: #f96167;
  text-decoration: none;
}

/* Adjust the size of the post video */
.post-vid {
  width: 100%;
  border-radius: 4px;
  margin-bottom: 5px;
}

/* Create space between icons */
.post-row {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

/* Adjust the size of the icon images */
.activity-icons div i {
  font-size: 18px;
  margin-right: 10px;
}

.activity-icons div button {
  width: 18px;
  height: 18px;
  color: var(--text-color);
  margin-right: 10px;
  background: transparent;
  border: none;
  cursor: pointer;
}

.like-on {
  color: #f96167;
}

/* Adjust alignment of the icons in the write post container*/
.activity-icons div {
  display: inline-flex;
  align-items: center;
  margin-right: 75px;
}

.post-profile-icon {
  display: flex;
  align-items: center;
}

/* Adjust size of profile pic in write post section */
.post-profile-icon img {
  width: 20px;
  border-radius: 50%;
  margin-right: 5px;
}

/* Change color of the three dot icon */
.post-row a {
  color: #9a9a9a;
}

/* Adjust position and style of the load more button */
.load-more-btn {
  display: block;
  margin: auto;
  cursor: pointer;
  padding: 5px 10px;
  border: 1px solid #9a9a9a;
  color: var(--text-color);
  background: transparent;
  border-radius: 4px;
}

/* Adjust copyright text font */
.footer {
  text-align: center;
  color: #9a9a9a;
  padding: 10px 0 20px;
  font-size: 13px;
}

/* Adjust location and size of settings menu */
.settings-menu {
  position: absolute;
  width: 90%;
  max-width: 350px;
  background: var(--bg-color);
  box-shadow: 0 0 1px #fff;
  border-radius: 4px;
  overflow: hidden;
  top: 108%;
  right: 5%;
  max-height: 0;
  transition: max-height 0.3s;
}

.settings-menu-height {
  max-height: 450px;
}

/* Adjust font of links inside drop down bar */
.user-profile a {
  font-size: 12px;
  color: #f96167;
  text-decoration: none;
}

.settings-menu-inner {
  padding: 20px;
}

/* Adjust size and color of horizontal line */
.settings-menu hr {
  border: 0;
  height: 1px;
  background: #9a9a9a;
  margin: 15px 0;
}

.settings-links {
  display: flex;
  align-items: center;
  margin: 15px 0;
}

/* Adjust size and shape of settings icons */
.settings-links .settings-icon {
  width: 38px;
  margin-right: 10px;
  border-radius: 50%;
}

/* Create space between the settings icons */
.settings-links a {
  display: flex;
  flex: 1;
  align-items: center;
  justify-content: space-between;
  text-decoration: none;
  color: var(--text-color);
}

/* Adjust the shape of the background of the toggle darkmode button */
#dark-btn {
  position: absolute;
  top: 20px;
  right: 20px;
  background: var(--dark-btn-color);
  width: 45px;
  border-radius: 15px;
  padding: 2px 3px;
  cursor: pointer;
  display: flex;
  transition: padding-left 0.5s, background 0.5s;
}

/* Adjust the shape of the toggle circle of the toggle darkmode button */
#dark-btn span {
  width: 18px;
  height: 18px;
  background: var(--toggle-circle-color);
  border-radius: 50%;
  display: inline-block;
}

/* Adjust when dark button is activated */
#dark-btn.dark-btn-on {
  padding-left: 23px;
}

.sub-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 5px;
  width: 80px;
  padding: 6px 14px;
  background-color: #626262;
  color: white;
  border: none;
  cursor: pointer;
  border-radius: 4px;
}

.sub-btn-on {
  background-color: #f96167;
  width: 90px;
  padding: 6px 16px;
}

.sub-btn-off {
  background-color: #626262;
  padding: 6px 14px;
}

.sidebar-title i {
  color: #f96167;
  font-size: 18px;
  justify-content: center;
  margin-left: 2px;
}

/* ____________ profile page ____________*/
.profile-container {
  padding: 20px 15%;
  color: #626262;
}

.cover-img {
  width: 100%;
  border-radius: 6px;
  margin-bottom: 14px;
}

.profile-details {
  background: var(--bg-color);
  padding: 20px;
  border-radius: 4px;
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
}

.profile-details-row {
  display: flex;
  align-items: flex-start;
}

.profile-details-images {
  width: 100px;
  margin-right: 20px;
  border-radius: 6px;
}

.profile-details-row div h3 {
  font-size: 25px;
  font-weight: 600;
  color: var(--text-color);
}

.profile-details-row div p {
  font-size: 13px;
  color: #9a9a9a;
}

.profile-details-row img {
  width: 25px;
  border-radius: 50%;
  margin-top: 12px;
}

.profile-details-right button {
  background: #f96167;
  border: 0;
  outline: 0;
  padding: 6px 10px;
  display: inline-flex;
  align-items: center;
  color: #fff;
  border-radius: 3px;
  margin-left: 10px;
  cursor: pointer;
}

.profile-details-right button img {
  height: 15px;
  margin-right: 10px;
}

.profile-details-right button:first-child {
  background: #e4e6eb;
  color: #000;
}

.profile-details-right {
  text-align: right;
}

.profile-details-right a {
  background: #e4e6eb;
  border-radius: 3px;
  padding: 12px;
  display: inline-flex;
  margin-top: 30px;
}

.profile-details-right a img {
  width: 20px;
}

.profile-info {
  display: flex;
  align-self: flex-start;
  justify-content: space-between;
  margin-top: 20px;
}

.info-col {
  flex-basis: 33%;
}

.post-col {
  flex-basis: 65%;
}

.profile-intro {
  background: var(--bg-color);
  padding: 20px;
  margin-bottom: 20px;
  /* border-radius: 4px;
  box-shadow: 0 0 5px #000; */
}

.profile-intro h3 {
  font-weight: 600;
  color: var(--text-color);
}

.intro-text {
  text-align: center;
  margin: 10px 0;
  font-size: 15px;
}

.profile-intro hr {
  border: 0;
  height: 1px;
  background: #ccc;
  margin: 24px 0;
}

.profile-intro p {
  font-size: 14px;
  color: #9a9a9a;
}

.profile-intro ul li {
  color: #9a9a9a;
}

.title-box {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.title-box a {
  text-decoration: none;
  color: #f96167;
  font-size: 14px;
}

.friends-box {
  display: grid;
  grid-template-columns: repeat(3, auto);
  grid-gap: 10px;
  margin-top: 15px;
}

.friends-box div img {
  width: 100%;
  cursor: pointer;
  padding-bottom: 20px;
}

.friends-box div {
  position: relative;
}

.friends-box p {
  position: absolute;
  bottom: 0;
  left: 0;
}

.pagination {
  margin-top: auto;
  padding: 0 3.5rem;
}

.pagination::after {
  content: "";
  display: table;
  clear: both;
}

.pagination__btn--prev {
  float: left;
}

.pagination__btn--next {
  float: right;
}

.btn--inline {
  color: var(--bg-color);
  font-size: 1.3rem;
  font-weight: 600;
  border: none;
  background-color: #f96167;
  padding: 0.8rem 1.2rem;
  border-radius: 10rem;
  cursor: pointer;

  display: flex;
  align-items: center;
  transition: all 0.2s;
}

.btn--inline svg {
  height: 1.6rem;
  width: 1.6rem;
  fill: currentColor;
  margin: 0 0.2rem;
}

.btn--inline span {
  margin: 0 0.4rem;
}

.btn--inline:hover {
  color: var(--bg-color);
  background-color: var(--video-title-color);
}

.btn--inline:focus {
  outline: none;
}
